const { fal } = require(""@fal-ai/client"");
const fs = require(""fs"");
const https = require(""https"");

fal.config({ credentials: process.env.FAL_KEY });

// Better conceptual graphics - less like infographics, more like hero imagery
const prompts = [
  {
    name: ""hero-spirit-network.png"",
    prompt: `Abstract network visualization on deep charcoal background. Minimalist geometric composition showing interconnected nodes forming a distributed network pattern. Nodes are simple white circles connected by thin white lines. One central cluster glows with subtle blue (#6B8FFF) accent. Clean, architectural aesthetic. No text. Swiss design influence. Extremely minimal, elegant, professional. Dark background #0A0A0A.`
  },
  {
    name: ""hero-revenue-flow.png"",
    prompt: `Abstract visualization of flow and distribution. A single stream of light entering from top, splitting into four equal paths flowing downward. Minimal geometric representation. White and blue (#6B8FFF) on charcoal (#0A0A0A) background. Clean lines, no decorative elements. Suggests value distribution without being literal. Swiss/Bauhaus influence. Professional, institutional aesthetic.`
  },
  {
    name: ""hero-identity-layers.png"",
    prompt: `Concentric circles visualization representing layers of identity. Multiple semi-transparent circles nested within each other, outer rings fading and dashed to suggest impermanence, inner core solid with blue (#6B8FFF) glow suggesting permanence. Minimal, architectural. No text. Dark charcoal background #0A0A0A. Swiss design, Helvetica aesthetic. Clean geometric shapes only.`
  },
  {
    name: ""hero-genesis-cohort.png"",
    prompt: `Four distinct geometric shapes arranged in a row, each slightly different but harmonious. Simple abstract symbols - perhaps a spiral, a wave pattern, overlapping circles, and a grid. White outlines on charcoal #0A0A0A background. One shape has subtle blue (#6B8FFF) accent. Represents four unique autonomous entities. Minimal, gallery-quality. Swiss design influence.`
  }
];

async function downloadImage(url, filepath) {
  return new Promise((resolve, reject) => {
    const file = fs.createWriteStream(filepath);
    https.get(url, (response) => {
      response.pipe(file);
      file.on(""finish"", () => { file.close(); resolve(); });
    }).on(""error"", reject);
  });
}

async function generateAll() {
  for (const item of prompts) {
    console.log(""Generating:"", item.name);
    try {
      const result = await fal.subscribe(""fal-ai/nano-banana-pro"", {
        input: { prompt: item.prompt, image_size: ""landscape_16_9"" }
      });
      const url = result.data.images[0].url;
      await downloadImage(url, ""generated-infographics/"" + item.name);
      console.log(""✓ Saved:"", item.name);
    } catch (error) {
      console.error(""✗ Failed:"", item.name, error.message);
    }
  }
  console.log(""Done!"");
}

generateAll();
')
